package audits;import configurations.CustomRevisionListener;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.envers.RevisionEntity;import org.hibernate.envers.RevisionNumber;import org.hibernate.envers.RevisionTimestamp;import javax.persistence.*;import java.io.Serializable;import java.util.Date;@Entity@Table(name = "revision_info")@RevisionEntity(CustomRevisionListener.class)@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Revision implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "revision_seq")    @SequenceGenerator(name = "revision_seq", sequenceName = "rbac.seq_revision_id")    @RevisionNumber    private Long id;    @Column(name = "revision_date")    @Temporal(TemporalType.TIMESTAMP)    @RevisionTimestamp    private Date date;}