package entities;import lombok.*;import org.hibernate.envers.Audited;import javax.persistence.*;import java.io.Serializable;@Entity@Table(name = "details")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@Auditedpublic class Detail implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long id;    @Column(name = "quantity")    private int quantity;    @Column(name = "subtotal")    private double subtotal;    @ManyToOne(cascade = CascadeType.PERSIST)    @JoinColumn(name = "article_id")    private Article article;    @ManyToOne(cascade = CascadeType.PERSIST)    @JoinColumn(name = "invoice_id")    private Invoice invoice;}